export function useClipboard() {
  const write = async (text, onSuccess, onError) => {
    try {
      await navigator.clipboard.writeText(text);
      if (onSuccess) onSuccess();
    } catch (error) {
      if (onError) {
        onError(error);
      } else {
        // eslint-disable-next-line no-console
        console.log(error);
      }
    }
  };

  return {
    write
  };
}
